/* === Underline === */
.underline {
	position: relative;
	padding-bottom: 4px;
	display: inline-block;
}
.underline::after {
	content: '';
	display: block;
	position: absolute;
	height: 0.8px;
	width: calc(100% - 1.3px);
	margin-top: 4px;
	margin-left: 0.9px;
	-webkit-transition: all 0.3s;
	-moz-transition: all 0.3s;
	-ms-transition: all 0.3s;
	-o-transition: all 0.3s;
	transition: all 0.3s;
}
.underline:hover::after,
.underline:focus::after {
	-webkit-transform: scale(1.15);
	-moz-transform: scale(1.15);
	transform: scale(1.15);
}

/* Colors */
.underline_color_masthead::after {
	background: var(--gray-green-color);
}
.underline_color_references::after {
	background: var(--light-green-color);
}
.underline_color_projects::after {
	background: white;
}


/*
* _____ FIRST _____
*/

/* === Section === */
.first {
	height: 100vh; /* Fallback for browsers that do not support Custom Properties */
	height: calc(var(--vh, 1vh) * 100);
	min-height: 440px;
	position: relative;
}
.first__background {
	background: var(--dark-green-color);
	position: absolute;
	z-index: -10;
	object-fit: cover;
	width: 100%;
	height: 100%;
}
.first__inner {
	position: relative;
	display: -webkit-flex;
	display: -moz-flex;
	display: -ms-flex;
	display: -o-flex;
	display: flex;
	-ms-align-items: center;
	align-items: center;
	height: 100%;
	color: white;
	text-transform: uppercase;
	text-align: center;
}


/* === Masthead === */
.masthead {
	position: absolute;
	top: 55px;
	right: 0;
	left: 0;
	display: -webkit-flex;
	display: -moz-flex;
	display: -ms-flex;
	display: -o-flex;
	display: flex;
	justify-content: space-between;
	letter-spacing: 1.5px;
}
.masthead__navigation {
	margin-left: 15px;
}


/* === Logo === */
.logo {
	display: block;
	font-size: 17em;
}


/* === Navigation === */
.navigation {
	font-size: 15em;
	font-weight: bold;
	display: -webkit-flex;
	display: -moz-flex;
	display: -ms-flex;
	display: -o-flex;
	display: flex;
	justify-content: space-between;
	width: 300px;
}


/* Items & animation */
.navigation__item-link {
	position: relative;
	display: inline-block;
}
.navigation__item-link::before,
.navigation__item-link::after {
	display: inline-block;
	opacity: 0;
	-webkit-transition: -webkit-transform 0.3s, opacity 0.2s;
	-moz-transition: -moz-transform 0.3s, opacity 0.2s;
	transition: transform 0.3s, opacity 0.2s;
}
.navigation__item-link::before {
	margin-right: 0;
	content: '[';
	-webkit-transform: translateX(10px);
	-moz-transform: translateX(10px);
	transform: translateX(10px);
}
.navigation__item-link::after {
	margin-left: 0;
	content: ']';
	-webkit-transform: translateX(-10px);
	-moz-transform: translateX(-10px);
	transform: translateX(-10px);
}
.navigation__item-link:hover::before,
.navigation__item-link:hover::after,
.navigation__item-link:focus::before,
.navigation__item-link:focus::after {
	opacity: 1;
	-webkit-transform: translateX(0px);
	-moz-transform: translateX(0px);
	transform: translateX(0px);
}


/* === Email === */
.email {
	font-size: 13em;
	text-transform: lowercase;
	position: relative;
}


/* === Title === */
.first__title {
	margin: 0 auto 15px auto;
}
.first__heading {
	font-size: 49em;
}
.first__caption {
	letter-spacing: 3px;
	font-size: 29em;
	font-weight: 100;
}


/* === Arrow to down === */
.first__arrow-to-down {
	position: absolute;
	left: 0;
	bottom: 82px;
	right: 0;
	margin: auto;
	height: 40px;
	width: 40px;
	font-size: 18em;
	border-radius: 50%;
	box-shadow: 0 0 0 1px rgba(255,255,255,1);
	overflow: hidden;
	-webkit-transition: all 0.3s;
	-o-transition: all 0.3s;
	transition: all 0.3s;
}
.first__arrow-to-down::before {
	display: block;
	content: 'â†“';
	line-height: 36px;
}

/* Hover animation */
.first__arrow-to-down:hover,
.first__arrow-to-down:focus {
	background: white;
	color: var(--green-color);
	box-shadow: 0 0 0 8px rgba(255,255,255,0.3);
}
.first__arrow-to-down:hover::before,
.first__arrow-to-down:focus::before {
	-webkit-animation: fromTopToBottom 0.3s forwards;
	-moz-animation: fromTopToBottom 0.3s forwards;
	animation: fromTopToBottom 0.3s forwards;
}

@-webkit-keyframes fromTopToBottom {
	49% {
		-webkit-transform: translateY(100%);
	}
	50% {
		opacity: 0;
		-webkit-transform: translateY(-100%);
	}
	51% {
		opacity: 1;
	}
}
@-moz-keyframes fromTopToBottom {
	49% {
		-moz-transform: translateY(100%);
	}
	50% {
		opacity: 0;
		-moz-transform: translateY(-100%);
	}
	51% {
		opacity: 1;
	}
}
@keyframes fromTopToBottom {
	49% {
		transform: translateY(100%);
	}
	50% {
		opacity: 0;
		transform: translateY(-100%);
	}
	51% {
		opacity: 1;
	}
}


/* === Media === */

/* Narrow navigation */
@media screen and (max-width: 875px) {
	.navigation {
		width: 250px;	
	}
	.navigation__item-link::before {
		margin-left: 2px;
	}
	.navigation__item-link::after {
		margin-right: 2px;
	}
}

/* Make masthead upright, remove navigation and decrease title */
@media screen and (max-width: 730px) {
	.masthead {
		-webkit-flex-direction: column;
		-moz-flex-direction: column;
		-ms-flex-direction: column;
		-o-flex-direction: column;
		flex-direction: column;	
	}
	.masthead__navigation {
		display: none;	
	}
	.logo {
		font-size: 20em;
	}
	.email {
		font-size: 11em;
		font-weight: 100;
		margin-top: 5px;
	}
	.first__title {
		font-size: 0.8px;
	}
}

/* Decrease heading */
@media screen and (max-width: 620px) {
	.first__heading {
		font-size: 36em;
	}
}

/* Decrease title */
@media screen and (max-width: 480px) {
	.first__title {
		font-size: 0.65px;
	}
	.first__heading {
		letter-spacing: 2px;
	}
	.first__caption {
		margin-top: 10px;
	}
}

/* Decrease heading */
@media screen and (max-width: 400px) {
	.first__title {
		font-size: 0.575px;
	}
	.first__heading {
		letter-spacing: 0.1px;
	}
}



/*
* _____ ABOUT ME _____
*/

/* === Content grid === */
.about-me__content {
	display: grid;
 	grid-template-columns: 1fr 1fr;
 	grid-template-rows: auto 1fr;
 	grid-gap: 20px;
 	grid-auto-flow: column;
}
.about-me__bg-info {
	grid-row-start: 1;
	grid-row-end:   3;
}


/* === Bg-info === */
.bg-info {
	padding-right: 15px;
}
.bg-info__text {
	font-size: 15em;
	margin-bottom: 2em;
	letter-spacing: 0.5px;
	line-height: 1.6em;
}


/* === Merits === */
.merits__list {
	position: relative;
	display: -webkit-flex;
	display: -moz-flex;
	display: -ms-flex;
	display: -o-flex;
	display: flex;
	justify-content: space-between;
}
.merits__item {
	width: 140px;
	text-align: center;
}
.merits__number {
	position: relative;
	margin: auto;
	font-size: 20em;
	color: var(--green-color);
	line-height: 102px;
	font-weight: 100;
	height: 102px;
	width: 102px;
}
.merits__number::after {
	content: '';
	display: block;
	position: absolute;
	top: 0;
	right: 0;
	left: 0;
	bottom: 0;
	margin: auto;
	height: 58px;
	width: 58px;
	border: 1px solid var(--light-green-color);
	outline: 1px solid var(--green-color);
	outline-offset: 7px;
	transform-origin: 50% 50%;
	transform: rotate(45deg);
	-webkit-transition: transform 0.3s;
	-moz-transition: transform 0.3s;
	-ms-transition: transform 0.3s;
	-o-transition: transform 0.3s;
	transition: transform 0.3s;
}
.merits__number:hover::after {
	transform: rotate(35deg);
}
.merits__name {
	font-size: 12em;
	text-transform: uppercase;
	margin-top: 20px;
}


/* === Connection === */
.connection {
	padding-top: 47px;
}
.connection__content {
  margin-top: 1em;
	font-size: 14em;
	display: -webkit-flex;
	display: -moz-flex;
	display: -ms-flex;
	display: -o-flex;
	display: flex;
	justify-content: space-between;
	-ms-align-items: flex-start;
	align-items: flex-start;
}

/* Referemces */
.references {
	margin-right: 10px;
}
.references__item {
	color: var(--green-color);
	margin-bottom: 17px;
}

/* Connect me */
.me {
	display: -webkit-flex;
	display: -moz-flex;
	display: -ms-flex;
	display: -o-flex;
	display: flex;
	-ms-align-items: center;
	align-items: center;
	margin-top: 3px;
}
.me__photo {
	border-radius: 50%;
	height: 62px;
	width: 62px;
	margin-right: 20px;
	background-color: #eef1ed;
}
.me__call {
	font-style: italic;
	text-align: center;
}


/* === Media quires === */

/* Middle element of merits higher */
